mapper.select:
select bno, title, insert_date, readcnt, recommend, reply
from board
	
limit #{pageStart}, #{perPageNum}

select moneybook_name, title, content, insert_date, readcnt, recommend, bno
from board
where bno = #{bno}

select moneybook_type, price, cartegory, board_date
from board_status
where bno = #{bno}

mapper.update:
update board
set readcnt = readcnt + 1
where bno = #{bno}

update board
set reply = reply + 1
where bno = #{bno}

update board
set reply = reply - 1
where bno = #{bno}

mapper.select:
select moneybook_name, title, content, bno
from board
where bno = #{bno}

mapper.update:
update board
set title = #{title}, content = #{content}, insert_date = #{insert_date}
where bno = #{bno}
