mapper.select:
select moneybook_name, title, content, insert_date, readcnt, recommend, bno
from board
where bno = #{bno}

select moneybook_type, price, cartegory, board_date
from board_status
where bno = #{bno}

mapper.update:
update board
set title = #{title}, content = #{content}
where bno = #{bno}
